	@RequestMapping(value="/{id}", method=RequestMethod.PUT, consumes = MediaType.APPLICATION_JSON_VALUE)
	//@PreAuthorize("hasRole('ADMIN')")
	@CrossOrigin(origins = "http://localhost:8080")
    public ResponseEntity<AdminDTO> updateAdmin(@RequestBody AdminDTO adminDTO, @PathVariable Long id){
        Admin admin;
        if(!this.validate(adminDTO))
    		return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
        try {
            admin = (Admin) adminDTO.toEntity();
        	admin = service.update(admin, id);
        } catch (Exception e) {
            return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
        }

        return new ResponseEntity<>((AdminDTO)(admin.toDTO()), HttpStatus.OK);
    }
	
	@RequestMapping(value="/{id}", method=RequestMethod.DELETE)
	//@PreAuthorize("hasRole('ADMIN')")
	@CrossOrigin(origins = "http://localhost:8080")
    public ResponseEntity<Void> deleteAdmin(@PathVariable Long id){
        try {
            service.delete(id);
        } catch (Exception e) {
            return new ResponseEntity<>(HttpStatus.NOT_FOUND);
        }

        return new ResponseEntity<>(HttpStatus.OK);
    }

