	private List<AdminDTO> toDTOAdminsList(List<Admin> admins) {
		List<AdminDTO> listDTO = new ArrayList<AdminDTO>();
        for (Admin u: admins) {
        	listDTO.add((AdminDTO)(u.toDTO()));
        }
        return listDTO;
	}

