import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;
import org.springframework.security.core.Authentication;
import org.springframework.security.core.context.SecurityContextHolder;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;

import com.sbnz.dto.AdminDTO;
import com.sbnz.dto.RegisteredUserDTO;
import com.sbnz.dto.UserDTO;
import com.sbnz.dto.UserLoginDTO;
import com.sbnz.dto.UserTokenStateDTO;
import com.sbnz.model.Admin;
import com.sbnz.model.RegisteredUser;
import com.sbnz.model.User;
import com.sbnz.repository.UserRepository;
import com.sbnz.security.TokenUtils;
import com.sbnz.service.AdminService;
import com.sbnz.service.CustomUserDetailsService;
