	@RequestMapping(value = "/{id}", method = RequestMethod.PUT, consumes = MediaType.APPLICATION_JSON_VALUE)
	//@PreAuthorize("hasAnyRole('ADMIN', 'REGISTERED_USER')")
	@CrossOrigin(origins = "http://localhost:4200")
	public ResponseEntity<RegisteredUserDTO> updateRegisteredUser(@RequestBody RegisteredUserDTO registeredUserDTO,
			@PathVariable Long id) {

		System.out.println(registeredUserDTO);
		RegisteredUser registeredUser;
		if (!this.validate(registeredUserDTO))
			return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
		try {
			registeredUser = service.update(registeredUserDTO.toEntity(), id);
		} catch (Exception e) {
			return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
		}

		return new ResponseEntity<>(registeredUser.toDTO(), HttpStatus.OK);
	}

	@RequestMapping(value = "/{id}", method = RequestMethod.DELETE)
	//@PreAuthorize("hasAnyRole('ADMIN', 'REGISTERED_USER')")
	@CrossOrigin(origins = "http://localhost:8080")
	public ResponseEntity<Void> deleteRegisteredUser(@PathVariable Long id) {

		try {
			service.delete(id);
		} catch (Exception e) {
			return new ResponseEntity<>(HttpStatus.NOT_FOUND);
		}

		return new ResponseEntity<>(HttpStatus.OK);
	}

