	private List<RegisteredUserDTO> toDTORegsUserList(List<RegisteredUser> users) {
		List<RegisteredUserDTO> listDTO = new ArrayList<RegisteredUserDTO>();
		for (RegisteredUser u : users) {
			listDTO.add(u.toDTO());
		}
		return listDTO;
	}

